mapper.select:
SELECT v.company_id AS companyId, v.vNum AS totalVehicle, d.dNUm AS totalDriver FROM (SELECT company_id, count(*) AS vNum FROM vehicle_info
WHERE update_time BETWEEN #{startDate,jdbcType=BIGINT} AND #{endDate,jdbcType=BIGINT}
GROUP BY company_id) v JOIN (SELECT company_id, count(*) AS dNUm FROM driver_info
WHERE update_time BETWEEN #{startDate,jdbcType=BIGINT} AND #{endDate,jdbcType=BIGINT}
GROUP BY company_id) d ON v.company_id = d.company_id
WHERE v.company_id=#{companyId,jdbcType=VARCHAR};
