  identifier, address, contact_address, 
  economic_type, legal_name, legal_phone, 
  state, flag, update_time
  )
values (#{id,jdbcType=INTEGER}, #{companyId,jdbcType=VARCHAR}, #{companyName,jdbcType=VARCHAR}, 
  #{identifier,jdbcType=VARCHAR}, #{address,jdbcType=INTEGER}, #{contactAddress,jdbcType=VARCHAR}, 
  #{economicType,jdbcType=VARCHAR}, #{legalName,jdbcType=VARCHAR}, #{legalPhone,jdbcType=VARCHAR}, 
  #{state,jdbcType=INTEGER}, #{flag,jdbcType=INTEGER}, #{updateTime,jdbcType=BIGINT}
  )
