mapper.select:
SELECT company_id AS companyId,count(*) AS vehicleAmount FROM (SELECT company_id,vehicle_no,count(*) AS amount FROM operate_depart_arrive
WHERE order_match_time BETWEEN #{startDate} AND #{endDate}
GROUP BY company_id, vehicle_no HAVING amount BETWEEN #{min} AND #{max}) d
WHERE company_id = #{companyId,jdbcType=VARCHAR}
